sys_ui_page.client_script:
function closewindow() {
    GlideModal.get().destroy();
}

function getRecord() {
    var grStock = new GlideAjax("getNumberOfStocks");
    grStock.addParam('sysparm_name', 'getStocks');
    grStock.addParam('sysparm_laptop_model', g_form.getValue('laptop_model'));
    grStock.getXML(returnStock);
}

function returnStock(response) {
    var result = response.responseXML.documentElement.getAttribute('answer');
    g_form.clearValue('stock');
    g_form.setValue('stock', result);
    GlideModal.get().destroy();
}
